sys_ui_action.script:
//Client-side 'onclick' function
function getIncidentID(){
	//Create and open the dialog form

	var lastIncID = getPreference('resilient_incident_id');

	var incID = prompt("Enter Resilient Incident ID to add task to:", lastIncID);
	if (incID){
		if (lastIncID != incID){
			setPreference('resilient_incident_id', incID);
		}
		//Call the UI Action and skip the 'onclick' function
		gsftSubmit(null, g_form.getFormElement(), 'create_resilient_task');
	}
}

//Code that runs without 'onclick'
//Ensure call to server-side function with no browser errors
if(typeof window == 'undefined')
	createTask();

function createTask() {
	action.setRedirectURL(current);
	var incID = gs.getUser().getPreference("resilient_incident_id");
	var creator;
	try{
		creator = new ResilientCreator();
	}
	catch (ex) {
		if (ex instanceof ReferenceError) {
			// no custom mapping defined, use default
			try{
				creator = new ResilientDefaultCreator();
			}
			catch (ex){
				gs.addInfoMessage("Failed to Create Resilient Task: " + ex.toString());
				throw ex;
			}
		} else {
			gs.addInfoMessage("Failed to Create Resilient Task: " + ex.toString());
			throw ex;
		}
	}

	creator.createTask(incID, current);

}
