sys_ws_operation.operation_script:
function stringToFunction(str) {
	var arr = str.split(".");

	var fn = this;
	for (var i = 0, len = arr.length; i < len; i++) {
		fn = fn[arr[i]];
	}

	if (typeof fn !== "function") {
		throw new Error("function not found");
	}

	return  fn;
}

(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var message = request.body.data;
	try{
		var className = gs.getProperty("x_166541_ibm_resil.ResilientHandlerClass");
		if (!className){
			response.setStatus(400);
			result = {
				"message": "ResilientHandlerClass property not set"
			};
		}
		else{
			var HandlerClass = stringToFunction(className);
			var handler = new HandlerClass();
			result = handler.update(message);
			if (typeof result === "string"){
				// an error message
				response.setStatus(400);
				result = {
					"message": "Failed to update ticket. " + result
				};
			}
			else{
				response.setStatus(201);
			}
		}
	}
	catch (ex){
		response.setStatus(500);
		result = {
			"message": "Failed to update ticket. " + ex.toString()
		};
	}
	response.setBody(result);
})(request, response);
