sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var message = request.body.data;
	var handler;
	try {
		handler = new ResilientHandler();
	}
	catch (ex) {
		if (ex instanceof ReferenceError) {
			// no custom handler defined, use default
			try{
				handler = new ResilientDefaultHandler();
			}
			catch (ex) {
				errorMsg = "Couldn't instantiate ResilientDefaultHandler" + ": " + ex.toString();
			}
		} else {
			errorMsg = "Couldn't instantiate custom ResilientHandler" + ": " + ex.toString();
		}
	}

	try{
		result = handler.create(message);
		if (typeof result === "string"){
			// an error message
			response.setStatus(400);
			result = {
				"message": "Failed to create ticket. " + result
			};
		}
		else{
			response.setStatus(201);
		}
	}
	catch (ex){
		response.setStatus(500);
		result = {
			"message": "Failed to create ticket. " + ex.toString()
		};
	}
	response.setBody(result);
})(request, response);
